import React from 'react';

import "./StepDescription.css"

export default class StepDescription extends React.Component {
    constructor(props) {
        super(props);
        this.textInputRef = React.createRef();

    }

    render() {

        var config = this.getConfig(this.props.mode);

        return <input id={"input-text_" + this.props.id.toString()}
                      key={this.props.id}
                      ref={this.textInputRef} 
                      type="text" 
                      placeholder="type here..." 
                      maxLength="30" 
                      size="30"
                      blur={config.blur} 
                      focus={config.focus}
                      readOnly={config.readOnly} 
                      style={config.style}
                      onClick={(event) => this.props.onSingleClick(this.props.id, this.textInputRef.current.value, event)}
                      onDoubleClick={() => this.props.onDoubleClick(this.props.id)}
                      onKeyPress={(event) => this.props.onKeyPress(this.props.id, event)}
                      onBlur={() => this.props.onBlur(this.props.id, this.textInputRef.current.value)} />
    }

    componentDidUpdate() {
        if (this.props.mode === "writing") { 
            this.textInputRef.current.selectionStart = this.textInputRef.current.selectionEnd;
        }

    }

    getConfig(mode) {
        let textInputStyle = {};
        let blur = "false";
        let focus = "true";
        let readOnly = false;

        if (mode === "writing") {
            textInputStyle = {cursor: "text", backgroundColor: "white", borderRadius: 5, maxWidth: 200};

        } else if (this.props.mode === "not_writing") {
            textInputStyle = {cursor: "pointer", backgroundColor: "#20D0FF", borderRadius: 5, maxWidth: 200};
            blur = "true";
            focus = "false";
            readOnly = true;
        }

        return {readOnly: readOnly, style: textInputStyle, focus: focus, blur: blur}
    }
}
