import React from 'react';
import { Draggable } from 'react-beautiful-dnd';

import "./StepDescription.css"

export default class StepDescription extends React.Component {
    constructor(props) {
        super(props);
        this.textInputRef = React.createRef();

    }

    render() {
        var config = this.getConfig(this.props.item);

        return <Draggable draggableId={this.props.id.toString()}>
            <input id={"input-text_" + this.props.id.toString()}
                key={this.props.id}
                ref={this.textInputRef}
                type="text"
                placeholder="type here..."
                maxLength="30"
                size="30"
                autoComplete="off"
                draggable={true}
                blur={config.blur}
                focus={config.focus}
                autoFocus={config.autofocus}
                readOnly={config.readOnly}
                style={config.style}
                onClick={(event) => this.props.onSingleClick(this.props.id, this.textInputRef.current.value, event)}
                onKeyDown={(event) => this.props.onKeyPress(this.props.id, this.textInputRef.current.value, event)}
                onBlur={() => this.props.onBlur(this.props.id, this.textInputRef.current.value)} />
        </Draggable>
    }

    componentDidUpdate() {
        this.textInputRef.current.value = this.props.item.text;
        if (this.props.item.mode === "writing") {
            this.textInputRef.current.focus();
        } else {
            this.textInputRef.current.blur();
        }
    }

    componentDidMount() {
        this.textInputRef.current.value = this.props.item.text;
        if (this.props.item.mode === "writing") {
            this.textInputRef.current.focus();
        } else {
            this.textInputRef.current.blur();
        }
    }

    getConfig(item) {

        const { mode, selected, level } = item;

        let textInputStyle = {};
        let blur = "false";
        let focus = "true";
        let autofocus = true;
        let readOnly = false;

        if (mode === "writing") {
            textInputStyle = { cursor: "text", backgroundColor: "#11112112" };

        } else if (mode === "not_writing") {
            textInputStyle = { cursor: "pointer", backgroundColor: "#20D0FF" };
            blur = "true";
            focus = "false";
            readOnly = true;
            autofocus = false;
        }

        // level-related styling
        textInputStyle.marginLeft = (level - 1) * 50;
        textInputStyle.marginTop = 12;
        textInputStyle.fontSize = 17;
        if (level === 2) {
            textInputStyle.marginTop = 4;
            textInputStyle.fontSize = 15;
        } else if (level === 3) {
            textInputStyle.marginTop = 2;
            textInputStyle.fontSize = 14;
        }

        return { readOnly: readOnly, style: textInputStyle, focus: focus, blur: blur, autofocus: autofocus }
    }
}
