import React from 'react'
import StepProposition from './StepProposition';



export default class DescriptionPane extends React.Component {
    constructor(props) {
        super(props);
        this.state = {propositions: [{key: 0, selected: true, txt: "", freq: null}]}
        this.counter = 0;
    }

    render() {
        const {rows, cols, options} = this.props;

        return <div id='proposition-pane-cantainer'>
            {this.state.propositions.map((proposition, index) => {
                let data = {selected: proposition.selected, freq: proposition.freq};
                <StepProposition key={proposition.key.toString()} id={proposition.key.toString()} rows={rows} cols={cols} options={options} data={data}></StepProposition>
            })}
        </div>;
    }


}
