import React from "react"

import StepDescription from "./StepDescription"

import "./DescriptionPane.css"


export default class DescriptionPane extends React.Component {
    constructor(props) {
        super(props);
        this.counter = 0;
        this.state = {descriptions: [{key: 0, mode: "writing", level: 1, selected: false}]};
        this.counter += 1;

        this.setModeByKey = this.setModeByKey.bind(this);
        this.getMode = this.getMode.bind(this);
        this.addStepAfter = this.addStepAfter.bind(this);
        this.removeWritingStep = this.removeWritingStep.bind(this);
        this.getSelected = this.getSelected.bind(this);
        this.deselectAll = this.deselectAll.bind(this);
        this.getIndex = this.getIndex.bind(this);

        this.handleSingleClick = this.handleSingleClick.bind(this);
        this.handleDoubleClick = this.handleDoubleClick.bind(this);
        this.handleBlur = this.handleBlur.bind(this);
        this.handleKeyAction = this.handleKeyAction.bind(this);


    }

    render() {
        let descriptions = this.state.descriptions;
        let descriptionList = descriptions.map((item) => 
                               <StepDescription id={item.key.toString()} 
                                                key={item.key.toString()} 
                                                onSingleClick={this.handleSingleClick} 
                                                onDoubleClick={this.handleDoubleClick}
                                                onBlur={this.handleBlur}
                                                onKeyPress={this.handleKeyAction} 
                                                mode={item.mode}
                                                selected={item.selected} 
                                                level={item.level}/>)
        return <div id="description-pane-container">{descriptionList}</div>

    }

    handleSingleClick(key, txt, event) {
        if (txt === "") {
            let items = this.setModeByKey(this.state.descriptions, key, "writing");
            this.setState({descriptions: items});
        } else if (event.shiftKey) { // it is non-writing
            const currentStepMode = this.getMode(this.state.descriptions, key, 0);
            const nextStepMode = this.getMode(this.state.descriptions, key, 1);
            if (currentStepMode === "not_writing" && (nextStepMode !== "writing" || nextStepMode === undefined)) {
                // remove existing writing step
                let items = this.removeWritingStep(this.state.descriptions)
                items = this.addStepAfter(items, key, "writing", 1);
                this.setState({descriptions: items});
            }
        }
    }

    handleDoubleClick(key) {
        let items = this.removeWritingStep(this.state.descriptions);
        items = this.deselectAll(items);
        // update the selected step
        let currentIndex = this.getIndex(items, key, 0);
        items[currentIndex].mode = "writing";
        if (currentIndex > 0) {
            items[currentIndex-1].selected = true;
        }
        this.setState({descriptions: items});
    }

    handleBlur(key, txt) {
        if (txt !== "") {
            let items = this.setModeByKey(this.state.descriptions, key, "not_writing");
            this.setState({descriptions: items});
        } else {

        }
    }

    handleKeyAction(key, txt, event) {
        if (event.which === 13 && txt !== "") {
            const currentStepMode = this.getMode(this.state.descriptions, key, 0);
            if (currentStepMode === "writing") {
                // change the current step to non-writing and add one writing step after it
                let items = this.setModeByKey(this.state.descriptions,key, "not_writing")
                items = this.addStepAfter(items, key, "writing", 1);
                this.setState({descriptions: items});
            }
        }

        // Del or backspace
        //    if non-writing
        //       delete the key

        // Space or Tab
        //    if it is non-writing
        //          indention 
        //    else 
        //         do nothing

        // up-down arrows
        // change the selected item
        // if (event.which) {
        //     index = this.getSelected(this.state.descriptions);
        // }

    }

    getIndex(stateItems, descriptionKey, offset) {
        let items = [...stateItems];
        let itemIndex = items.map(item => item.key).indexOf(parseInt(descriptionKey));
        if (itemIndex >= 0) {
            return itemIndex + offset;
        }
        return -1;
    }

    setModeByKey(stateItems, descriptionKey, mode) {
        let items = [...stateItems];
        let itemIndex = items.map(item => item.key).indexOf(parseInt(descriptionKey));
        if (itemIndex >= 0) {
            items[itemIndex].mode = mode;
        } else {
            console.log("step description does not exist")
        }

        return items;

    }


    getMode(stateItems, descriptionKey, offset) {
        let items = [...stateItems];
        let itemIndex = items.map(item => item.key).indexOf(parseInt(descriptionKey));
        if (items[itemIndex + offset] !== undefined) {
            return items[itemIndex + offset].mode; 
        } 
        return undefined
    }


    getSelected(items) {
        for (let i = 0; i < items.length; i++) {
            if (items[i].selected) {
                return i;
            }
        }
        return -1
    }

    deselectAll(items) {
        for (let i = 0; i < items.length; i++) {
            if(items[i].selected) {
                items[i].selected = false;
            }
        }
        return items
    }

    addStepAfter(itemsState, key, mode, level) {
        let items = [...itemsState];
        let itemIndex = items.map(item => item.key).indexOf(parseInt(key));
        if (items[itemIndex].mode !== "writing") {
            // deselect all
            items = this.deselectAll(items)
            // select the current step
            items[itemIndex].selected = true;
            // add the new step
            items.splice(itemIndex+1, 0, {key: this.counter, mode: mode, level: level, selected: false})
            this.counter += 1
        }
        return items;
    }

    removeWritingStep(itemsState) {
        let items = [...itemsState];
        let newItems = [...items];
        for (let i = 1; i < items.length; i++) {
            if (items[i].mode === "writing") {
                console.log(i)
                newItems.splice(i, 1)
                return newItems
            }
        }
        console.log("Didn't find the item, to remove")
        return newItems  

    }
}
