import React from 'react';

import "./StepDescription.css"

export default class StepDescription extends React.Component {
    constructor(props) {
        super(props);
        this.state = { value: "" }

        this.handleDoubleClick = this.handleDoubleClick.bind(this);
        this.handleSingleClick = this.handleSingleClick.bind(this);
        this.handleDescriptionBlur = this.handleDescriptionBlur.bind(this);
        this.handleDescriptionFocus = this.handleDescriptionFocus.bind(this);
    }

    handleDoubleClick(event) {
        console.log("double click")
    }

    handleSingleClick(event) {
        // transform the text to editable text
        console.log("single click")
    }

    handleDescriptionBlur(event) {
        console.log("description blur")
        // blur style
        document.getElementById("description-text").style.backgroundColor = "#BCA9F5"
    }

    handleDescriptionFocus(event) {
        console.log("focus")
        // focus style
        document.getElementById("description-text").style.backgroundColor = "#F2F2F2"

    }

    render() {
        return <textarea name="description"
            id="description-text"
            placeholder="type here..."
            cols="30"
            rows="2"
            onFocus={this.handleDescriptionFocus}
            onBlur={this.handleDescriptionBlur}
            style={{ backgroundColor: "#F2F2F2", cursor: "pointer" }}>
        </textarea>
    }

