import React from "react"

import StepDescription from "./StepDescription"


export default class DescriptionPane extends React.Component {
    constructor(props) {
        super(props);
        this.counter = 0;
        this.state = {descriptions: [{key: 0, mode: "writing", level: 1}]};
        this.counter += 1;

        this.handleSingleClick = this.handleSingleClick.bind(this);
        this.handleDoubleClick = this.handleDoubleClick.bind(this);
        this.handleBlur = this.handleBlur.bind(this);

    }

    handleSingleClick(key) {
        let items = [...this.state.descriptions];
        let itemIndex = items.map(item => item.key).indexOf(parseInt(key));
        if (itemIndex >= 0) {
            items[itemIndex].current.disabled = "disabled"
        }
    }

    handleDoubleClick(key) {
    }

    handleBlur(key, txt) {
        // if it is writing and non-empty make it solid (not writinh)
        if (txt !== "") {
            let items = [...this.state.descriptions];
            let itemIndex = items.map(item => item.key).indexOf(parseInt(key));
            if (itemIndex >= 0) {
                items[itemIndex].mode = "not_writing";
            } else {
                console.log("step description does not exist")
            }

            this.setState({items});
        }
        // if it solid remove the writing input
        //TODO:
    }

    render() {
        let descriptions = this.state.descriptions;
        let descriptionList = descriptions.map((item) => 
                               <StepDescription id={item.key.toString()} 
                                                key={item.key.toString()} 
                                                onSingleClick={this.handleSingleClick} 
                                                onDoubleClick={this.handleDoubleClick}
                                                onBlur={this.handleBlur} 
                                                mode={item.mode} 
                                                level={item.level}/>)
        return <div>{descriptionList}</div>

    }
}
