import React, { Component } from "react";

export default class StepProposition extends React.Component {
    constructor(props) {
        super(props);
    }

    render() {
        const { rows, cols, selected, options } = this.props;
        let stepProposition = null;

        if (selected) {
            stepProposition = (
                <div className="step-proposition-container" style={{borderBottom: "solid", borderWidth: "1px"}}>
                    <textarea
                        rows={rows}
                        cols={cols}
                        placeholder={"type here"}
                        style={{ resize: "none", overflow: "hidden", padding: "10px", marginBottom: "15px" }}
                    ></textarea>
                    {options.length ? (
                        <div className="frequency-scale-form-container">
                            <form>
                                <div
                                    className="frequency-scale-radio-btn-container"
                                    style={{
                                        display: "flex",
                                        flexDirection: "row",
                                        alignItems: "center",
                                        justifyContent: "center",
                                        gap: "60px"
                                    }}
                                >
                                    {options.map((option) => (
                                        <div
                                            className="scale-radio-btn-div"
                                            style={{
                                                display: "flex",
                                                flexDirection: "column",
                                                alignItems: "center",
                                            }}
                                        >
                                            <label
                                                id={`radio-btn-label-${option}`}
                                                for={`radio-btn-${option}`}
                                                style={{marginBottom: "4px"}}
                                            >
                                                {option}
                                            </label>
                                            <input
                                                id={`radio-btn-${option}`}
                                                type="radio"
                                                name="freqScale"
                                                value={option}
                                            ></input>
                                        </div>
                                    ))}
                                </div>
                            </form>
                        </div>
                    ) : (
                        {}
                    )}
                </div>
            );
        }

        return stepProposition;
    }
}
