import React from 'react';

import "./StepDescription.css"

export default class StepDescription extends React.Component {
    constructor(props) {
        super(props);
        this.textInputRef = React.createRef();

    }

    render() {

        var textInputStyle = {};
        var blur = "false";
        var focus = "true";
        var readOnly = false;
        if (this.props.mode === "writing") {
            textInputStyle = {cursor: "text", backgroundColor: "white", borderRadius: 5};

        } else if (this.props.mode === "not_writing") {
            textInputStyle = {cursor: "pointer", backgroundColor: "#20D0FF", borderRadius: 5};
            blur = "true";
            focus = "false";
            readOnly = true;
        }

        return <input id="input-text" 
                      key={this.props.id}
                      ref={this.textInputRef} 
                      type="text" 
                      placeholder="type here..." 
                      maxLength="30" 
                      size="30"
                      blur={blur} 
                      focus={focus}
                      readOnly={readOnly} 
                      style={textInputStyle}
                      onClick={() => this.props.onSingleClick(this.props.id, this.textInputRef.current.value)}
                      onDoubleClick={() => this.props.onDoubleClick(this.props.id)}
                      onBlur={() => this.props.onBlur(this.props.id, this.textInputRef.current.value)} />
    }

    componentDidMount() {
        if (this.props.mode === "writing") { 
        this.textInputRef.current.selectionStart = this.textInputRef.current.selectionEnd;
        }

    }
}


// Single click 
// do not change the mode
// reject writing

// Double click
// go to wiring mode

// Blur
// go to non-writing mode


// What happens in non-writing mode
// cursor --> pointer
// background --> customized color
// input is in blur (unfocus)

// What happens in writing mode
// cursor --> text
// background --> white
// input is in focus
