import React from 'react';


export default class DescriptionPane extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            value: ""
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleKeyPressed = this.handleKeyPressed.bind(this);
    }

    handleChange(event) {
        this.setState({ value: event.target.value });
    }

    handleKeyPressed(event) {
        if (event.charCode === 13 && event.shiftKey) {
            console.log("shift + enter")
        }
    }

    render() {
        return <div id="main-container">
            <div id="scrollable-div">
                <textarea placeholder="Describe the activity here..." rows="2" cols="25" value={this.state.value} onKeyPress={this.handleKeyPressed} onChange={this.handleChange} />
            </div>
        </div>
    }
}
