import React from 'react';

import "./StepDescription.css"

export default class StepDescription extends React.Component {
    constructor(props) {
        super(props);
        this.textInputRef = React.createRef();


    }

    componentDidMount() {
        if (this.props.mode === "writing") {
            this.textInputRef.current.style.cursor = "text";
        } else if (this.props.mode === "not_writing") {
            this.textInputRef.current.blur();
            this.textInputRef.current.style.backgroundColor = "red";
            this.textInputRef.current.style.cursor = "pointer";
        }
    }


    render() {

        var textInputStyle = {};
        if (this.props.mode === "writing") {
            textInputStyle = {cursor: "text", backgroundColor: "white"};
        } else if (this.props.mode === "not_writing") {
            textInputStyle = {cursor: "pointer", backgroundColor: "red"};
        }

        return <input id="input-text" 
                      key={this.props.id}
                      ref={this.textInputRef} 
                      type="text" 
                      placeholder="type here..." 
                      maxLength="30" 
                      size="30" 
                      readOnly={this.props.mode === "unselected"} 
                      style={textInputStyle} 
                      onClick={() => this.props.onSingleClick(this.props.id)}
                      onDoubleClick={() => this.props.onDoubleClick(this.props.id)}
                      onBlur={() => this.props.onBlur(this.props.id, this.textInputRef.current.value)} />
    }
}
