import React, { Component } from "react";

export default class StepProposition extends React.Component {
    constructor(props) {
        super(props);
    }

    render() {
        const { rows, cols, selected, options } = this.props;
        let stepProposition = null;

        if (selected) {
            stepProposition = (
                <div className="step-proposition-container">
                    <textarea
                        rows={rows}
                        cols={cols}
                        placeholder={"type here"}
                        style={{ resize: "none", overflow: "hidden", padding: "10px" }}
                    ></textarea>
                    {options.length ? (
                        <div className="frequency-scale-container">
                            <form>
                                {options.map((option) => (
                                    <div className="scale-radio-btn-div" style={{display: "flex", flexDirection: "column"}}>
                                        <input
                                            id={`radio-btn-${option}`}
                                            type="radio"
                                            name="freqScale"
                                            value={option}
                                        ></input>
                                        <label id={`radio-btn-label-${option}`} for={`radio-btn-${option}`}>{option}</label>
                                    </div>
                                ))}
                            </form>
                        </div>
                    ) : (
                        {}
                    )}
                </div>
            );
        }

        return stepProposition;
    }
}
