import React from "react";
import StepProposition from "./StepProposition";

export default class DescriptionPane extends React.Component {
    constructor(props) {
        super(props);
        this.state = { propositions: [{ key: 0, selected: true, text: "", freq: null }] };
        this.counter = 0;

        this.handleFreqencyChanged = this.handleFreqencyChanged.bind(this);
    }

    render() {
        const { rows, cols, options } = this.props;

        console.log(this.state.propositions)

        return (
            <div id="proposition-pane-cantainer">
                {this.state.propositions.map((proposition) => (
                    <StepProposition
                        key={proposition.key.toString()}
                        id={proposition.key.toString()}
                        rows={rows}
                        cols={cols}
                        options={options}
                        data={proposition}
                        onFreqChanged={this.handleFreqencyChanged}
                    ></StepProposition>
                ))}
            </div>
        );
    }


    handleFreqencyChanged(propositionId, optionIdx) {
        console.log(event)
    }

}
