import React from "react";

export default class StepProposition extends React.Component {
    constructor(props) {
        super(props);
        this.textAreaRef = React.createRef();
    }

    render() {
        const { rows, cols, data, options } = this.props;
        let stepProposition = null;

        stepProposition = (
            <div className="step-proposition-container" style={{ padding: "10px", boxShadow: "0px 3px 6 #AAAAAA33" }}>
                <textarea
                    readOnly={!data.selected}
                    ref={this.textAreaRef}
                    rows={data.selected ? rows : 1}
                    cols={cols}
                    placeholder={"type here"}
                    style={{
                        resize: "none",
                        overflow: "hidden",
                        padding: "10px",
                        marginBottom: "15px",
                        fontSize: "18"
                    }}
                    onBlur={(event) => {}} // TODO:
                ></textarea>
                {options.length ? (
                    <div className="frequency-scale-form-container">
                        <form>
                            <div
                                className="frequency-scale-radio-btn-container"
                                style={{
                                    display: "flex",
                                    flexDirection: "row",
                                    alignItems: "center",
                                    justifyContent: "center",
                                    gap: "60px",
                                }}
                            >
                                {options.map((option, idx) => (
                                    <div
                                        className="scale-radio-btn-div"
                                        style={{
                                            display: "flex",
                                            flexDirection: "column",
                                            alignItems: "center",
                                        }}
                                    >
                                        <label
                                            id={`radio-btn-label-${option}`}
                                            for={`radio-btn-${option}`}
                                            style={{ marginBottom: "4px" }}
                                        >
                                            {option}
                                        </label>
                                        <input
                                            id={`radio-btn-${option}`}
                                            type="radio"
                                            name="freqScale"
                                            value={option}
                                            checked={data.freq === option ? true : false}
                                            onClick={() =>
                                                this.props.onFreqSelected(
                                                    parseInt(this.props.id),
                                                    idx,
                                                    this.textAreaRef.current.textContent
                                                )
                                            }
                                        ></input>
                                    </div>
                                ))}
                            </div>
                        </form>
                    </div>
                ) : (
                    {}
                )}
            </div>
        );

        return stepProposition;
    }

    componentDidUpdate() {
        this.textAreaRef.current.textContent = this.props.data.text;
        console.log(this.props.data.text.split("\n")[0]);
        if (!this.props.selected) {
            this.textAreaRef.current.textContent = this.props.data.text.split("\n")[0];
        }
    }

    componentDidMount() {
        this.textAreaRef.current.textContent = this.props.data.text;
    }
}
