import React from 'react';


export default class DescriptionText extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            value: ""
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    handleChange(event) {
        this.setState({ value: event.target.value });
    }

    handleKeyPressed(event) {
        if (event.charCode == 13 && event.shiftKey) {
            console.log("shift + enter")
        }
    }

    handleSubmit(event) {
        alert('An essay was submitted: ' + this.state.value);
        event.preventDefault();
    }

    render() {
        return (
            <form id="text-form" onSubmit={this.handleSubmit}>
                <label>
                    Activity description
                 <textarea placeholder="Describe the activity here..." rows="12" cols="70" value={this.state.value} onKeyPress={this.handleKeyPressed} onChange={this.handleChange} />
                </label>
                <input type="submit" value="Submit" />
            </form>
        );
    }
}
