import React, { useRef } from "react";

function TaskDescription(props) {

    let pRef = useRef();

    return (
        <React.Fragment>
            <h5 style={{ marginBottom: "3px" }}>Task description</h5>
            <p
                ref={pRef}
                style={{
                    wordWrap: "break-word",
                    boxSizing: "border-box",
                    resize: "none",
                    height: "auto",
                    width: props.width,
                    fontSize: 17,
                    padding: 20,
                    background: "#ECEEF7",
                    borderRadius: 5,
                    borderColor: "#2DB9F8",
                    color: "#434548"
                }}
            >
                {props.description}
            </p>
        </React.Fragment>
    );

    useEffect(() => {
        pRef.current.textContent = props.description;
        return () => {

        }
    }, [])
}

export default TaskDescription;
