import React from 'react';

import "./StepDescription.css"

export default class StepDescription extends React.Component {
    constructor(props) {
        super(props);
        this.textInputRef = React.createRef();

        this.handleDoubleClick = this.handleDoubleClick.bind(this);
        this.handleSingleClick = this.handleSingleClick.bind(this);
        this.setSelectedStyle = this.setSelectedStyle.bind(this);
        this.handleDescriptionBlur = this.handleDescriptionBlur.bind(this);

    }

    handleDoubleClick(event) {
        this.textInputRef.current.readOnly = false;
        this.setSelectedStyle();
        this.props.onDoubleClick(this.props.id);
        this.textInputRef.current.selected = false;

    }

    handleSingleClick(event) {
        this.textInputRef.current.readOnly = true;
        this.setUnselectedStyle();
        this.props.onSingleClick(this.props.id);
    }

    handleDescriptionBlur(event) {
        this.textInputRef.current.readOnly = true;
        this.setUnselectedStyle();
        this.props.onBlur(this.props.id, this.textInputRef.current.value)
    }


    componentDidMount() {
        if (this.props.mode === "selected") {
            this.textInputRef.current.focus();
        } else if (this.props.mode === "unselected") {
            this.textInputRef.current.blur();
        }
    }

    setSelectedStyle() {
        this.textInputRef.current.style.cursor = "text";
    }

    setUnselectedStyle() {
        this.textInputRef.current.style.cursor = "pointer";
    }

    render() {

        var textInputStyle = {};
        if (this.props.mode === "selected") {
            textInputStyle = {cursor: "text"};
        } else if (this.props.mode === "unselected") {
            textInputStyle = {cursor: "pointer"};
        }

        return <input id="input-text" 
                      key={this.props.id}
                      ref={this.textInputRef} 
                      type="text" 
                      placeholder="type here..." 
                      maxLength="30" 
                      size="30" 
                      readOnly={this.props.mode === "unselected"} 
                      style={textInputStyle} 
                      onClick={this.handleSingleClick}
                      onDoubleClick={this.handleDoubleClick}
                      onBlur={this.handleDescriptionBlur} />
    }
}
